import React, { useState, useEffect } from 'react';

const Tracking = () => {
  const [trackingId, setTrackingId] = useState('');
  const [trackingData, setTrackingData] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleTrackingSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Simulate API call for tracking
      setTimeout(() => {
        setTrackingData({
          orderId: trackingId,
          status: 'In Transit',
          currentLocation: 'Distribution Center - City A',
          estimatedDelivery: '2025-09-27T14:30:00Z',
          timeline: [
            { status: 'Order Placed', timestamp: '2025-09-26T09:00:00Z', location: 'Online' },
            { status: 'Picked Up', timestamp: '2025-09-26T11:00:00Z', location: 'Pickup Location' },
            { status: 'In Transit', timestamp: '2025-09-26T15:00:00Z', location: 'Distribution Center - City A' },
          ]
        });
        setLoading(false);
      }, 1500);
    } catch (error) {
      console.error('Error fetching tracking data:', error);
      setLoading(false);
    }
  };

  return (
    <div className="tracking">
      <h2>Track Your Order</h2>
      
      <form onSubmit={handleTrackingSubmit}>
        <div className="form-group">
          <label htmlFor="trackingId">Tracking ID:</label>
          <input
            type="text"
            id="trackingId"
            value={trackingId}
            onChange={(e) => setTrackingId(e.target.value)}
            placeholder="Enter your tracking ID"
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Tracking...' : 'Track Order'}
        </button>
      </form>

      {trackingData && (
        <div className="tracking-results">
          <h3>Order Status</h3>
          <div className="status-overview">
            <p><strong>Order ID:</strong> {trackingData.orderId}</p>
            <p><strong>Current Status:</strong> {trackingData.status}</p>
            <p><strong>Current Location:</strong> {trackingData.currentLocation}</p>
            <p><strong>Estimated Delivery:</strong> {new Date(trackingData.estimatedDelivery).toLocaleString()}</p>
          </div>

          <div className="timeline">
            <h4>Tracking Timeline</h4>
            {trackingData.timeline.map((event, index) => (
              <div key={index} className="timeline-event">
                <div className="event-status">{event.status}</div>
                <div className="event-details">
                  <p>{event.location}</p>
                  <p>{new Date(event.timestamp).toLocaleString()}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Tracking;
